   queryTransaction "${hash}"  "jq -r .result.receipt.tyName" "ExecOk"

   balance=$(${Chain33_CLI} account balance -a "0xa42431Da868c58877a627CC71Dc95F01bf40c196" -e coins | jq -r ".balance")
