}
function main() {
     echo "====================DAPP=${DAPP} main begin==================="
    ### start docker
    echo "#### start docker"
    start_docker
      ### test cases ###
    ip=$(${Chain33_CLI} net info | jq -r ".externalAddr")
    ip=$(echo "$ip" | cut -d':' -f 1)
    if [ "$ip" == "127.0.0.1" ]; then
        ip=$(${Chain33_CLI} net info | jq -r ".localAddr")
        ip=$(echo "$ip" | cut -d':' -f 1)
    fi
   # ip="127.0.0.1"
    MAIN_HTTP=http://${ip}:8801
    ETH_HTTP=http://${ip}:8545
    echo "main_http:${MAIN_HTTP}"
    run_testcase
    check_docker_container
    #finish
    docker-compose down
    echo "===============DAPP=$DAPP main end==============="
    echo "++++++++++++++++DAPP=$DAPP ci2  start++++++++++++++++"
    mkdir -p ../evm2-ci && cp ../evm2/* ../evm2-ci
    cp -n ../* ../evm2-ci && echo $?
    cd ../evm2-ci  && pwd
    ./docker-compose.sh build evm2
    echo "++++++++++++++++DAPP=$DAPP ci2  end++++++++++++++++"
}

