	pubInput := "000000020e362c7578039fcdf533d7c4490cab0de14ad33f9aefa628582f0ce04c4660b8000000000000000000000000000000000000000000000000000000003b9aca00"
	proof := ""

	rst, err := Verify(verifyKey, proof, pubInput)
	assert.Nil(t, err)
